spec.query:
marathon-game-data.marathon-game-data.aggregate([
  {
    '$match': {
      'scenario name': '${game}',
      'level index': '${levelIndex}'
    }
  },
  {
    '$group': {
      _id: null,
      pistolFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$pistol bullets fired',
              0
            ]
          }
        }
      },
      pistolHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$pistol bullets hit',
              0
            ]
          }
        }
      },
      rifleFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$rifle bullets fired',
              0
            ]
          }
        }
      },
      rifleHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$rifle bullets hit',
              0
            ]
          }
        }
      },
      grenade_launcherFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$grenades fired',
              0
            ]
          }
        }
      },
      grenade_launcherHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$grenades hit',
              0
            ]
          }
        }
      },
      flame_throwerFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$flamethrower bursts fired',
              0
            ]
          }
        }
      },
      flame_throwerHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$flamethrower bursts hit',
              0
            ]
          }
        }
      }
    }
  },
  {
    '$project': {
      _id: 0,
      weapons: [
        {
          name: 'Pistol',
          image: 'https://raw.githubusercontent.com/petewall/marathon-dashboards/refs/heads/main/images/weapons/pistol-m1.png',
          fired: '$pistolFired',
          hits: '$pistolHits',
          accuracy: {
            '$cond': [
              {
                '$eq': [
                  '$pistolFired',
                  0
                ]
              },
              0,
              {
                '$divide': [
                  '$pistolHits',
                  '$pistolFired'
                ]
              }
            ]
          }
        },
        {
          name: 'Rifle',
          image: 'https://raw.githubusercontent.com/petewall/marathon-dashboards/refs/heads/main/images/weapons/rifle-m1.png',
          fired: '$rifleFired',
          hits: '$rifleHits',
          accuracy: {
            '$cond': [
              {
                '$eq': [
                  '$rifleFired',
                  0
                ]
              },
              0,
              {
                '$divide': [
                  '$rifleHits',
                  '$rifleFired'
                ]
              }
            ]
          }
        },
        {
          name: 'Grenade Launcher',
          image: 'https://raw.githubusercontent.com/petewall/marathon-dashboards/refs/heads/main/images/weapons/rifle-m1.png',
          fired: '$grenade_launcherFired',
          hits: '$grenade_launcherHits',
          accuracy: {
            '$cond': [
              {
                '$eq': [
                  '$grenade_launcherFired',
                  0
                ]
              },
              0,
              {
                '$divide': [
                  '$grenade_launcherHits',
                  '$grenade_launcherFired'
                ]
              }
            ]
          }
        },
        {
          name: 'Flame Thrower',
          image: 'https://raw.githubusercontent.com/petewall/marathon-dashboards/refs/heads/main/images/weapons/flame-m1.png',
          fired: '$flame_throwerFired',
          hits: '$flame_throwerHits',
          accuracy: {
            '$cond': [
              {
                '$eq': [
                  '$flame_throwerFired',
                  0
                ]
              },
              0,
              {
                '$divide': [
                  '$flame_throwerHits',
                  '$flame_throwerFired'
                ]
              }
            ]
          }
        }
      ]
    }
  },
  {
    '$unwind': '$weapons'
  },
  {
    '$replaceRoot': {
      newRoot: '$weapons'
    }
  }
])

