  {
    '$match': {
      'scenario name': '${game}',
      'level index': '${levelIndex}'
    }
  },
  {
    '$group': {
      _id: null,
      pistolFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$pistol bullets fired',
              0
            ]
          }
        }
      },
      pistolHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$pistol bullets hit',
              0
            ]
          }
        }
      },
      fusion_pistolFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$fusion bolt minors fired',
              0
            ]
          }
        }
      },
      fusion_pistolHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$fusion bolt minors hit',
              0
            ]
          }
        }
      },
      charged_fusion_pistolFired: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$fusion bolt majors fired',
              0
            ]
          }
        }
      },
      charged_fusion_pistolHits: {
        '$sum': {
          '$toInt': {
            '$ifNull': [
              '$fusion bolt majors hit',
              0
