marathon-game-data.marathon-game-data.aggregate([
  {
    $match: {
      'scenario name': '${game}',
      'level index': '${levelIndex}'
    }
  },
  {
    $group: {
      _id: null,
      completions: {
        $sum: { $toInt: { $ifNull: ['$level completed', 0] } }
      },
      deaths: {
        $sum: { $toInt: { $ifNull: ['$deaths', 0] } }
      },
      playtime: {
        $sum: {
          $subtract: [
            { $toInt: { $ifNull: ['$end tick', 0] } },
            { $toInt: { $ifNull: ['$start tick', 0] } }
          ]
        }
      }
    }
  },
  {
    $project: {
      _id: 0,
      completions: 1,
      deaths: 1,
      playtime: 1
    }
  }
])
